mapper.update:
update zz_code_meeting_function
code = #{params.code},
name = #{params.name},
is_use = #{params.isUse},
upd_time = sysdate, upd_user = #{params.updUser}, upd_host = #{params.updHost}, upd_name = #{params.updName}
where id = #{params.id}

mapper.delete:
delete  from zz_code_meeting_function where 1=1

    and id = #{params.id}


    and code = #{params.code}
