mapper.select:
select
  GROUP_ID, GROUP_NAME, GROUP_DESCRIBE, ISDELETE, CREATE_TIME, CREATOR, UPDATE_TIME, UPDATOR, PNAME, SCOP, ISPUBLIC, LEVELS, ISCLOSE, GROUP_IMG, ISCROSS,CREATOR_NAME,GROUP_OWNER_ID,GROUP_OWNER_NAME
from ZZ_GROUP


        and GROUP_ID = #{groupId}


        and GROUP_NAME = #{groupName}


        and GROUP_DESCRIBE = #{groupDescribe}


        and ISDELETE = #{isdelete}


        and CREATE_TIME = #{createTime}


        and CREATOR = #{creator}


        and UPDATE_TIME = #{updateTime}


        and UPDATOR = #{updator}


        and PNAME = #{pname}


        and SCOP = #{scop}


        and ISPUBLIC = #{ispublic}


        and LEVELS = #{levels}


        and ISCLOSE = #{isclose}


        and GROUP_IMG = #{groupImg}

    order  by CREATE_TIME desc

mapper.update:
update ZZ_GROUP
   set ISDELETE = #{delFlg}
where GROUP_ID = #{groupId}

mapper.select:
select group_id, group_name, group_describe, to_char(create_time, 'yyyy-mm-dd hh24:mi:ss') create_time, creator, pname, scop, ispublic, levels, isclose, group_img, (select to_char(count(1)) from zz_user_group u where u.group_id = g.group_id) as member_nums, iscross, group_owner_id, group_owner_name from zz_group g where 1=1
and g.group_name like '%'||#{params.group_name}||'%'
and g.levels = #{params.level}
and to_char(g.create_time, 'yyyy-mm-dd') >= #{params.dateBegin}
and to_char(g.create_time, 'yyyy-mm-dd') <= #{params.dateEnd}
and g.pname like '%'||#{params.pname}||'%'
and g.isclose = #{params.isclose}
and g.iscross = #{params.iscross}
and g.GROUP_OWNER_NAME = like '%'||#{params.groupOwnerName}||'%'
