mapper.select:
select
  ID, SENDER, REVISER, READTIME,SENDERNAME, REVISERNAME
from WORKHUB_IWORK.ZZ_MEG_READ_LOG


        and ID = #{id}


        and SENDER = #{sender}


        and REVISER = #{reviser}


        and READTIME = #{readtime}

select
  ID, SENDER, REVISER, READTIME,SENDERNAME, REVISERNAME
from WORKHUB_IWORK.ZZ_MEG_READ_LOG


        and SENDERNAME = #{senderName}


        and REVISERNAME = #{reviserName}


order by READTIME desc
