mapper.select:
select
  MSG_ID, SENDER, RECEIVER, CREATETIME, LEVELS, CONTENT, TYPE, IP, senderSN
from WORKHUB_IWORK.ZZ_MESSAGE_INFO


        and MSG_ID = #{msgId}


        and SENDER = #{sender}


        and RECEIVER = #{receiver}


        and CREATETIME = #{createtime}


        and LEVELS = #{levels}


        and CONTENT = #{content}


        and TYPE = #{type}

select content from ( select content,createtime from zz_message_info where 1 = 1 and receiver = #{userId} and sender = #{contactId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type='USER' union select content,createtime from zz_message_info where 1 = 1 and receiver = #{contactId} and sender = #{userId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type='USER' ) a order by a.createtime desc

select content from zz_message_info mes where 1 = 1 and receiver = #{contactId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type = 'GROUP' and exists (select 1 from zz_user_group c where 1 = 1 and c.user_id = #{userId} and c.group_id = #{contactId} and c.createtime <= mes.createtime ) order by mes.createtime desc

select content from zz_message_info mes where 1 = 1 and receiver = #{contactId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type = 'MEET' and exists (select 1 from zz_meeting_user c where 1 = 1 and c.user_id = #{userId} and c.meeting_id = #{contactId} and c.crt_time <= mes.createtime ) order by mes.createtime desc

select msg_id id,content,to_char(createtime, 'yyyy-mm-dd HH24:MI:SS') sendtime ,ip, sender_sn from zz_message_info where 1 = 1 and type = 'USER' and content like '%"type":1,%'
and to_char(createtime, 'yyyy-mm-dd') <= #{params.timeEnd}
and to_char(createtime, 'yyyy-mm-dd') >= #{params.timeBegin}
and substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'|| #{params.senderName} || '%'
and substr(content, instr(content, ',"toName":"', 1, 1) + length(',"toName":"'), instr(content, '","id":"', 1, 2) - instr(content, ',"toName":"', 1, 1) - length(',"toName":"')) like '%'|| #{params.receiverName} || '%'
and levels = #{params.messageLevel}
and substr(content, instr(content, '","type":1,"title":"', 1, 1) + length('","type":1,"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, '","type":1,"title":"', 1, 1) - length('","type":1,"title":"')) like '%'|| #{params.messageContent} || '%'
order by createtime desc

select msg_id id,content,to_char(createtime, 'yyyy-mm-dd HH24:MI:SS') sendtime,ip, sender_sn from zz_message_info where 1 = 1 and type = 'GROUP' and content like '%"type":1,%'
and to_char(createtime, 'yyyy-mm-dd') <= #{params.timeEnd}
and to_char(createtime, 'yyyy-mm-dd') >= #{params.timeBegin}
and substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'|| #{params.senderName} || '%'
and substr(content, instr(content, ',"toName":"', 1, 1) + length(',"toName":"'), instr(content, '","id":"', 1, 2) - instr(content, ',"toName":"', 1, 1) - length(',"toName":"')) like '%'|| #{params.receiverName} || '%'
and levels = #{params.messageLevel}
and substr(content, instr(content, '","type":1,"title":"', 1, 1) + length('","type":1,"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, '","type":1,"title":"', 1, 1) - length('","type":1,"title":"')) like '%'|| #{params.messageContent} || '%'
order by createtime desc
