mapper.select:
select content from ( select content,createtime from zz_message_info where 1 = 1 and receiver = #{userId} and sender = #{contactId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type='USER' and cancel = '0' union select content,createtime from zz_message_info where 1 = 1 and receiver = #{contactId} and sender = #{userId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type='USER' and cancel = '0' ) a order by a.createtime desc

select msg_id, TYPE, senderid, sendername, senderavatar, senderlevels, senderpid, receiverid, receivername, receiveravatar, receivermembernum, receiverlevels, receivergroupowner, receiverpid, fileid, fileext, filesize, levels, frontid, filetype, msg, to_char(createtime ,'yyyy-mm-dd hh24:mi:ss') createtime from ( select t.msg_id, T.TYPE, sender.id senderid, sender.name sendername, sender.avartar senderavatar, sender.levels senderlevels, sender.pid senderpid, receiver.id receiverid, receiver.name receivername, receiver.avartar receiveravatar, receiver.member_num receivermembernum, receiver.levels receiverlevels, receiver.group_owner receivergroupowner, receiver.pid receiverpid, f.file_id fileid, f.file_ext fileext, to_char(f.sizes) filesize, t.levels levels, t.front_id frontid, t.file_type filetype, t.msg msg, t.createtime from zz_message_info t,zz_contact_inf sender,zz_contact_inf receiver,zz_group_file f where 1 = 1 and t.sender = sender.id and t.receiver = receiver.id and t.file_id = f.file_id(+) and t.receiver = #{userId} and t.sender = #{contactId}
and ( sender.name like '%'||#{query}||'%' or t.msg like '%'||#{query}||'%' )
and t.type='USER' and t.cancel = '0' union select t.msg_id, T.TYPE, sender.id senderid, sender.name sendername, sender.avartar senderavatar, sender.levels senderlevels, sender.pid senderpid, receiver.id receiverid, receiver.name receivername, receiver.avartar receiveravatar, receiver.member_num receivermembernum, receiver.levels receiverlevels, receiver.group_owner receivergroupowner, receiver.pid receiverpid, f.file_id fileid, f.file_ext fileext, to_char(f.sizes) filesize, t.levels levels, t.front_id frontid, t.file_type filetype, t.msg msg, t.createtime from zz_message_info t,zz_contact_inf sender,zz_contact_inf receiver,zz_group_file f where 1 = 1 and t.sender = sender.id and t.receiver = receiver.id and t.file_id = f.file_id(+) and t.receiver = #{contactId} and t.sender = #{userId}
and ( sender.name like '%'||#{query}||'%' or t.msg like '%'||#{query}||'%' )
and t.type='USER' and t.cancel = '0' ) a order by a.createtime desc

select content from zz_message_info mes where 1 = 1 and receiver = #{contactId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type = 'GROUP' and cancel = '0' and exists (select 1 from zz_user_group c where 1 = 1 and c.user_id = #{userId} and c.group_id = #{contactId} and c.createtime <= mes.createtime ) order by mes.createtime desc

select t.msg_id, T.TYPE, sender.id senderid, sender.name sendername, sender.avartar senderavatar, sender.levels senderlevels, sender.pid senderpid, receiver.id receiverid, receiver.name receivername, receiver.avartar receiveravatar, receiver.member_num receivermembernum, receiver.levels receiverlevels, receiver.group_owner receivergroupowner, receiver.pid receiverpid, f.file_id fileid, f.file_ext fileext, to_char(f.sizes) filesize, t.levels levels, t.front_id frontid, t.file_type filetype, t.msg msg, to_char(t.createtime,'yyyy-mm-dd HH24:MI:SS') createtime from zz_message_info t,zz_contact_inf sender,zz_contact_inf receiver,zz_group_file f where 1 = 1 and t.sender = sender.id and t.receiver = receiver.id and t.file_id = f.file_id(+) and t.receiver = #{contactId}
and ( sender.name like '%'||#{query}||'%' or t.msg like '%'||#{query}||'%' )
and t.type = 'GROUP' and t.cancel = '0' and exists (select 1 from zz_user_group c where 1 = 1 and c.user_id = #{userId} and c.group_id = #{contactId} and c.createtime <= t.createtime ) order by t.createtime desc

select content from zz_message_info mes where 1 = 1 and receiver = #{contactId}
and ( substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'||#{query}||'%' or substr(content, instr(content, ',"title":"', 1, 1) + length(',"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, ',"title":"', 1, 1) - length(',"title":"')) like '%'||#{query}||'%' )
and type = 'MEET' and cancel = '0' and exists (select 1 from zz_meeting_user c where 1 = 1 and c.user_id = #{userId} and c.meeting_id = #{contactId} and c.crt_time <= mes.createtime ) order by mes.createtime desc

select t.msg_id, T.TYPE, sender.id senderid, sender.name sendername, sender.avartar senderavatar, sender.levels senderlevels, sender.pid senderpid, receiver.id receiverid, receiver.name receivername, receiver.avartar receiveravatar, receiver.member_num receivermembernum, receiver.levels receiverlevels, receiver.group_owner receivergroupowner, receiver.pid receiverpid, f.file_id fileid, f.file_ext fileext, to_char(f.sizes) filesize, t.levels levels, t.front_id frontid, t.file_type filetype, t.msg msg, to_char(t.createtime,'yyyy-mm-dd HH24:MI:SS') createtime from zz_message_info t,zz_contact_inf sender,zz_contact_inf receiver,zz_group_file f where 1 = 1 and t.sender = sender.id and t.receiver = receiver.id and t.file_id = f.file_id(+) and t.receiver = #{contactId}
and ( sender.name like '%'||#{query}||'%' or t.msg like '%'||#{query}||'%' )
and t.type = 'MEET' and t.cancel = '0' and exists (select 1 from zz_meeting_user c where 1 = 1 and c.user_id = #{userId} and c.meeting_id = #{contactId} and c.crt_time <= t.createtime ) order by t.createtime desc

select msg_id id,content,to_char(createtime, 'yyyy-mm-dd HH24:MI:SS') sendtime ,ip, sender_sn from zz_message_info where 1 = 1 and type = 'USER' and cancel = '0' and content like '%"type":1,%'
and to_char(createtime, 'yyyy-mm-dd') <= #{params.timeEnd}
and to_char(createtime, 'yyyy-mm-dd') >= #{params.timeBegin}
and substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'|| #{params.senderName} || '%'
and substr(content, instr(content, ',"toName":"', 1, 1) + length(',"toName":"'), instr(content, '","id":"', 1, 2) - instr(content, ',"toName":"', 1, 1) - length(',"toName":"')) like '%'|| #{params.receiverName} || '%'
and levels = #{params.messageLevel}
and substr(content, instr(content, '","type":1,"title":"', 1, 1) + length('","type":1,"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, '","type":1,"title":"', 1, 1) - length('","type":1,"title":"')) like '%'|| #{params.messageContent} || '%'
order by createtime desc

select t.msg_id, T.TYPE, sender.id senderid, sender.name sendername, sender.avartar senderavatar, sender.levels senderlevels, sender.pid senderpid, receiver.id receiverid, receiver.name receivername, receiver.avartar receiveravatar, receiver.member_num receivermembernum, receiver.levels receiverlevels, receiver.group_owner receivergroupowner, receiver.pid receiverpid, f.file_id fileid, f.file_ext fileext, to_char(f.sizes) filesize, t.levels levels, t.front_id frontid, t.file_type filetype, t.msg msg, to_char(t.createtime,'yyyy-mm-dd HH24:MI:SS') createtime, t.ip from zz_message_info t,zz_contact_inf sender,zz_contact_inf receiver,zz_group_file f where 1 = 1 and t.sender = sender.id and t.receiver = receiver.id and t.file_id = f.file_id(+) and t.type = #{params.teamType} and t.file_type = '1' and t.cancel = '0'
and to_char(t.createtime, 'yyyy-mm-dd') <= #{params.timeEnd}
and to_char(t.createtime, 'yyyy-mm-dd') >= #{params.timeBegin}
and sender.name like '%'|| #{params.senderName} || '%'
and receiver.name like '%'|| #{params.receiverName} || '%'
and t.levels = #{params.messageLevel}
and t.msg like '%'|| #{params.messageContent} || '%'
order by t.createtime desc

select msg_id id,content,to_char(createtime, 'yyyy-mm-dd HH24:MI:SS') sendtime,ip, sender_sn from zz_message_info where 1 = 1 and type = 'GROUP' and cancel = '0' and content like '%"type":1,%'
and to_char(createtime, 'yyyy-mm-dd') <= #{params.timeEnd}
and to_char(createtime, 'yyyy-mm-dd') >= #{params.timeBegin}
and substr(content, instr(content, ',"username":"', 1, 1) + length(',"username":"'), instr(content, '"}', 1, 1) - instr(content, ',"username":"', 1, 1) - length(',"username":"')) like '%'|| #{params.senderName} || '%'
and substr(content, instr(content, ',"toName":"', 1, 1) + length(',"toName":"'), instr(content, '","id":"', 1, 2) - instr(content, ',"toName":"', 1, 1) - length(',"toName":"')) like '%'|| #{params.receiverName} || '%'
and levels = #{params.messageLevel}
and substr(content, instr(content, '","type":1,"title":"', 1, 1) + length('","type":1,"title":"'), instr(content, '","secretLevel":', 1, 1) - instr(content, '","type":1,"title":"', 1, 1) - length('","type":1,"title":"')) like '%'|| #{params.messageContent} || '%'
order by createtime desc
